assess = [],students = [],assess_jsn=[],attempts=[];

	json_data.map(function(d,i) { 
		assess.push(d.category);
		students.push(d.student);
	});
	assess = distinctVal(assess);
	students = distinctVal(students);

	students = students.slice(0,50);

var change_json_data = [];

    json_data.map(function(d){
    	if ($.inArray(d.category,assess)!=-1 && $.inArray(d.student,students)!=-1) {
    		change_json_data.push(d);
    	}
    });
		    
    json_data = change_json_data;

var margin = { top: 50, right: 0, bottom: 100, left: 90 },
        width = 960 - margin.left - margin.right,
        height = 500 - margin.top - margin.bottom;

var old_width = width,old_height= height;

var category_fixed = 20,student_fixed=9;

    width = Math.floor((assess.length * width)/category_fixed);

    height = Math.floor((students.length * height)/student_fixed);

    width = Math.ceil(width*2/3);

var xScale = d3.scale.ordinal()
    .rangeRoundBands([0, width],0.5,0.4);

var yScale = d3.scale.ordinal()
    .rangeRoundBands([height, 0],0.1,0.3);
	
var xAxis = d3.svg.axis()
    .scale(xScale)
    .orient("bottom")
    .tickSize(0);

var yAxis = d3.svg.axis()
    .scale(yScale)
    .orient("left")
    .tickSize(0);

var svgContainer = d3.select('#chartID').append("svg")
	.attr("width", width + margin.left + margin.right)
	.attr("height", height + margin.top + margin.bottom)
	.append("g")
	.attr("transform", "translate(" + (margin.left) + "," + (margin.top) + ")");

var series = json_data.map(function(d){ return d.status;});
	series = $.unique(series);

var status_color = {'Passed':'#42bc43','Failed':'#ff5353','Skipped':'#fec418','Not started':'#19abdc'};
var colours = d3.scale.ordinal().range(["#42bc43","#ff5353","#fec418","#19abdc"]);

update(students,assess);

// Update function for drawing X-AXIS ,Y-AXIS and Heat Rectangles

function update(students,assess){
		svgContainer.attr("class", "svgContainer");
  
		xScale.domain(assess.map(function(d,i){
			return d;
		}));
		yScale.domain(students.map(function(d,i){					
			return d;					
		}));
		
		svgContainer.append("g")
			.attr("class", "x axis")
			.attr("transform", "translate(0," + (height) + ")")
			.call(xAxis)
			.selectAll("text")
			.attr("data-value", function(d) { return d; });

	    svgContainer.append("g")
			.attr("class", "y axis")
			.call(yAxis)
			.selectAll("text")
			.attr("data-value", function(d) { return d; });

	var heatMap = svgContainer.selectAll(".status").append("g")
		    .data(json_data)
		    .enter();

	    heatMap.append("rect")
		    .attr("x", function(d,i) { 
				return xScale(d.category);							
		    })
	        .attr("width",25)
	        .attr("height",25)
	        .attr("y", function(d,i) { 
				return yScale(d.student);
	        })
	        .transition()
		    .duration(1000)
	        .style("fill", "#ffffd9")
	     	.transition().duration(2000)
	        .style("fill", function(d) { 
	      		return status_color[d.status];
	       });	
}

	function distinctVal(arr){
	    var newArray = [];
	    for(var i=0, j=arr.length; i<j; i++){
	        if(newArray.indexOf(arr[i]) == -1)
	              newArray.push(arr[i]);  
	    }
	    return newArray;
	}

	d3.selectAll("#chartID .y.axis .tick text").attr("x",-12).attr("y","-5");
	d3.selectAll("#chartID .x.axis .tick text").attr("x",-35).attr("y","5");

d3.select(window).on('resize', resize); 

 function resize() {
  // update width
   width = parseInt(d3.select('#chartID').style('width'));
   width = width - margin.left - margin.right;

   height = parseInt(d3.select("#chartID").style("height"));
   height = height - margin.top - margin.bottom;
     // resize the chart
    if(width<1000){
    xScale.rangeRoundBands([0, width],.1);
    yScale.range([height, 0]);

    d3.select(svgContainer.node().parentNode)
         .attr('width', (width + margin.left + margin.right));

    svgContainer.selectAll(".g").attr("transform", function(d) { return "translate(" + xScale(d.category) + ",0)"; }); 

    svgContainer.selectAll("rect")
                 .attr("x",function(d) { return xScale(d.category) })
                 .attr("width", xScale.rangeBand())

     svgContainer.select('.x.axis').call(xAxis.orient('bottom')).selectAll('text').attr("x",-35);

    }
 }